        <section class="ftr-sec2">
            <h2 class="ftr-sec2-title">categories</h2>
            <div class="ftr-sec2-cnt">
                <?php
                    // Fetch parent categories
                    $parent_categories = get_terms(array(
                        'taxonomy'   => 'category',
                        'hide_empty' => false,
                        'parent'     => 0, // Only get parent categories
                    ));

                    if (!empty($parent_categories)) {
                        echo '<ul class="ftr-cat-ul">'; // First UL for parent categories
                        foreach ($parent_categories as $parent) {
                            echo '<li class="ftr-cat-li"><a href="' . get_category_link($parent->term_id) . '">' . $parent->name . '</a></li>';
                        }
                        echo '</ul>'; // Close parent UL
                    }

                    // Fetch subcategories separately
                    if (!empty($parent_categories)) {
                        foreach ($parent_categories as $parent) {
                            $subcategories = get_terms(array(
                                'taxonomy'   => 'category',
                                'hide_empty' => false,
                                'parent'     => $parent->term_id, // Get only subcategories of this parent
                            ));

                            if (!empty($subcategories)) {
                                foreach ($subcategories as $subcategory) {
                                    echo '<li class="ftr-subcat-li"> <a href="' . get_category_link($subcategory->term_id) . '">' . $subcategory->name . '</a></li>';
                                }
                            }
                        }
                    }
                ?>
            </div>
        </section>
        <section class="ftr-sec3">
            <div class="ftr-sec3-pages">
                <h2 class="ftr-sec3-title">about us</h2>
                <ul class="ftr-pages-ul">
                    <?php
                        $pages = get_pages(array(
                            'parent' => 0, // Only get pages that are not child pages
                            'nopaging' => true, // Don't display pagination
                        ));
                        foreach ($pages as $page) {
                            echo '<li class="ftr-page-li"><a href="'. get_permalink($page->ID). '">'. $page->post_title. '</a></li>';
                        }
                        wp_reset_postdata();
                        ?>
                </ul>
            </div>
            <div class="ftr-sec3-logo">
                <div class="ftr-sec3-child1">
                    <figure class="ftr-logo-img-cnt">
                         <?php 
                        if (has_custom_logo()) {
                            the_custom_logo(); // Dynamically fetch WordPress logo
                        } else {
                            echo '<img class=".ftr-img" src="' . esc_url(get_template_directory_uri() . '/T.png') . '" alt="Site Logo">';
                        }
                    ?>
                    </figure>
                    <h2  aria-colspan="logo-text-cnt" class="ftr-web-title"><?php bloginfo('name'); ?></h2>
                </div>
            </div>
